mapper.select:
SELECT reserv_time
FROM reservation
WHERE h_id = #{hospitalId}
AND reserv_date = #{date}

SELECT id, email, name, phone
FROM user
WHERE id = #{userId}

SELECT pet_id, pet_name,  COALESCE(pet_picture, 'petdefault.png') AS pet_picture
FROM pet
WHERE user_id = #{userId}

SELECT r.reserv_id, r.reserv_date, p.pet_picture, p.pet_name, h.h_name, r.reserv_status FROM reservation r JOIN pet p ON r.pet_id = p.pet_id JOIN hospital h ON r.h_id = h.h_id WHERE DATE(r.reserv_date) < CURDATE() AND r.user_id = #{id}
AND p.pet_id = #{pet_id}
AND r.reserv_date >= #{startDate}
AND r.reserv_date <= #{endDate}
AND r.reserv_status = '진료완료'
order by reserv_date desc limit #{row}, 10

SELECT count(*)
FROM reservation r
JOIN pet p
ON r.pet_id = p.pet_id
JOIN hospital h ON r.h_id = h.h_id
WHERE DATE(r.reserv_date) < CURDATE()
AND r.user_id = #{id}


	AND p.pet_id = #{pet_id}


	AND r.reserv_date >= #{startDate}


	AND r.reserv_date <= #{endDate}


	AND r.reserv_status = '진료완료'

mapper.update:
UPDATE reservation
SET reserv_status = '예약취소'
WHERE pet_id = #{petId}
AND reserv_status != '예약취소';
