mapper.select:
SELECT * FROM hospital_qna
WHERE h_id = #{hospitalId}
ORDER BY q_time DESC
	LIMIT #{limit} OFFSET #{offset};

mapper.update:
UPDATE hospital_qna
SET a_title = #{hospitalQna.a_title}, a_time = NOW()
WHERE qna_id = #{qnaId};

mapper.select:
